sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	try { 
	instance=gs.getProperty('instance_name');
	if (instance.indexOf('dev')>-1){
		outgoing_api = gs.getProperty('x_cio19_optus_ate.outgoing_API_DEV');
	}else{
	if (instance.indexOf("uat")>-1){
		outgoing_api=gs.getProperty('x_cio19_optus_ate.outgoing_API_UAT');
	}else{
	outgoing_api=gs.getProperty('x_cio19_optus_ate.outgoing_API_PROD');
	}}
	if (gs.getProperty('x_cio19_optus_ate.Debug')=='True'){
		gs.info("OPTUS ATE Environment: " + instance+', '+outgoing_api);
		}	

 var s = new sn_ws.SOAPMessageV2(outgoing_api, 'sendTicketEvent');

//override authentication profile 
//authentication type ='basic'
//r.setAuthentication(authentication type,profile name);
try{
 s.setStringParameterNoEscape('WorkLog.WorkLog', current.worklog);
 s.setStringParameterNoEscape('instance.CloseTime', current.closetime);
 s.setStringParameterNoEscape('instance.Location', current.location);
 s.setStringParameterNoEscape('instance.CustomerParentID', current.customerparentid);
 s.setStringParameterNoEscape('instance.CSNSParentID', current.csnsparentid);
 s.setStringParameterNoEscape('instance.FirstName', current.firstname);
 s.setStringParameterNoEscape('instance.WorkStationPort', current.workstationport);
 s.setStringParameterNoEscape('instance.ErrorMessage', current.errormessagedescription);
 s.setStringParameterNoEscape('instance.ManagedTicketID', current.customerticketid);
 s.setStringParameterNoEscape('instance.DowntimeStart', current.downtimestart);
 s.setStringParameterNoEscape('instance.ContactKey', current.contactkey);
 s.setStringParameterNoEscape('UpdateDescription.UpdateDescription', current.updatedescrption);
 s.setStringParameterNoEscape('extensions.ExtensionsFieldName', '');
}
		catch(a){
			gs.error("OPTUS ATE 21: Error at a",a);
		}

try{
	var mapMessageStatus=new tcc_table_mapping();
	mapMessageStatus.getMappedInBoundValue(current.messagecompany,'messagestatus',current.messagestatus);		
	s.setStringParameterNoEscape('instance.MessageStatus', mapMessageStatus);	
 //s.setStringParameterNoEscape('instance.MessageStatus', current.messagestatus);

 s.setStringParameterNoEscape('instance.CustomerTicketID', current.number);
 s.setStringParameterNoEscape('instance.MobileNumber', current.mobilenumber);
 s.setStringParameterNoEscape('instance.ConfigItemID', current.configuration_item_id);
 s.setStringParameterNoEscape('instance.ProductType', current.producttype);
s.setStringParameterNoEscape('instance.company', current.company);

	var mapCategory=new tcc_table_mapping();
	mapCategory.getMappedInBoundValue(current.messagecompany,'category',current.category);		
	s.setStringParameterNoEscape('instance.Category', mapCategory);
//s.setStringParameterNoEscape('instance.Category', current.category);
}
		catch(b){
			gs.error("OPTUS ATE 21: Error at b",b);
		}

try{		
 //s.setStringParameterNoEscape('instance.Impact', current.impact);
	var mapImpact=new tcc_table_mapping();
	mapImpact.getMappedInBoundValue(current.messagecompany,'impact',current.impact);

		 s.setStringParameterNoEscape('instance.Impact', mapImpact);
 s.setStringParameterNoEscape('instance.AssignmentGroup', current.assignment_group);
 s.setStringParameterNoEscape('instance.ClosureCode', current.closure_code);
 s.setStringParameterNoEscape('instance.CostCentre', current.cost_centre);
// s.setStringParameterNoEscape('instance.Integrator', '');
 s.setStringParameterNoEscape('instance.PhoneNumber', current.phonenumber);
 s.setStringParameterNoEscape('messages.message', 'severity="${messages.message}" module="${messages.message}"');
 s.setStringParameterNoEscape('instance.ProblemType', current.problemtype);
 s.setStringParameterNoEscape('instance.DowntimeEnd', current.downtimeend);
 s.setStringParameterNoEscape('instance.EmailAddress', current.emailaddress);
	s.setStringParameterNoEscape('instance.company', current.company);
}
		catch(c){
			gs.error("OPTUS ATE 21: Error at c",c);
		}

try{
//s.setStringParameterNoEscape('instance.Priority', current.priority);
			var mapPriority=new tcc_table_mapping();
	mapPriority.getMappedInBoundValue(current.messagecompany,'priority',current.priority);

s.setStringParameterNoEscape('instance.Priority', mapPriority);	
 s.setStringParameterNoEscape('BriefDescription.BriefDescription', current.brief_description);
 s.setStringParameterNoEscape('instance.CustomerTicketStatus', current.messagestatus);
 s.setStringParameterNoEscape('extensions.ExtensionsFieldValue', '');
 s.setStringParameterNoEscape('instance.OpenTime', current.opentime);
 s.setStringParameterNoEscape('instance.CSNSTicketID', current.csnsticketid);
 s.setStringParameterNoEscape('instance.MessageCompany', current.messagecompany);
 s.setStringParameterNoEscape('instance.LastName', current.lastname);
 s.setStringParameterNoEscape('instance.PlannedEnd', current.plannedend);
 s.setStringParameterNoEscape('instance.MessageType', current.messagetype);
 s.setStringParameterNoEscape('instance.Urgency', current.urgency);
 s.setStringParameterNoEscape('instance.UniqueID', current.sys_id);
 s.setStringParameterNoEscape('instance.MessageDate', current.messagedate);
 s.setStringParameterNoEscape('instance.PlannedStart', current.plannedstart);
 s.setStringParameterNoEscape('instance.SubCategory', current.subcategory);
	s.setStringParameterNoEscape('instance.company', current.company);
 s.setStringParameterNoEscape('instance.AttachName', current.attachment_file_name);
 s.setStringParameterNoEscape('incident.attachments', 'href="${attachments.attachment}" contentId="${attachments.attachment}" action="${attachments.attachment}" name="${attachments.attachment}" type="${attachments.attachment}" len="${attachments.attachment}" charset="${attachments.attachment}" attachmentType="${attachments.attachment}">cid:2251074713');
}
		catch(d){
			gs.error("OPTUS ATE 21: Error at d",d);
		}

var response = s.execute();
 var responseBody = response.getBody(); 
 var status = response.getStatusCode();
	if (gs.getProperty('x_cio19_optus_ate.Debug')=='True'){
		gs.info('OPTUS ATE BR-21 OPEN REQUEST: ' +responseBody+', '+response+', '+status);
	}



}
catch(ex) { 
// var message = JSON.parse(ex.message());
	gs.error('OPTUS ATE BR-21 OPEN REQUEST: ',ex.message);
	//gs.debug('ATE_TCC_OpenReq_21 Not Sent');
gs.eventQueue('x_cio19_optus_ate.alerterror',current,gs.getProperty('x_cio19_optus_ate.Alert_Notifications'),'OPTUS ATE BR-21 OPEN REQUEST: ' +ex.message);
}

})(current, previous);
